mapper.select:
SELECT COUNT(*) FROM t_auth_user WHERE del = 0
AND username like CONCAT('%',#{username},'%' )
AND status = #{status }

SELECT
FROM t_auth_user WHERE del = 0
AND username like CONCAT('%',#{param1.username},'%' )
AND status = #{param1.status }
ORDER BY id LIMIT #{param2.startIndex, jdbcType=INTEGER} , #{param2.pageSize, jdbcType=INTEGER}

SELECT 

FROM t_auth_user
WHERE id = #{id, jdbcType=INTEGER}

SELECT 

FROM t_auth_user
WHERE username = #{username, jdbcType=VARCHAR}
AND  password = #{password, jdbcType=VARCHAR}

mapper.delete:
DELETE 
FROM t_auth_user
WHERE id = #{id, jdbcType=INTEGER}

mapper.update:
UPDATE t_auth_user
SET del = 1
WHERE id = #{id, jdbcType=INTEGER}
