mapper.select:
SELECT 

FROM t_user_collections 


        AND user_id = #{userId, jdbcType=INTEGER}


        AND object_id = #{objectId, jdbcType=INTEGER}


        AND classify = #{classify, jdbcType=INTEGER}


ORDER BY id

SELECT 

FROM t_user_collections
WHERE id = #{id, jdbcType=INTEGER}

mapper.update:
UPDATE t_user_collections
SET del = 1
WHERE id = #{id, jdbcType=INTEGER}
