mapper.select:
SELECT 

FROM t_user_course_section 


        AND user_id = #{userId, jdbcType=INTEGER}


        AND course_id = #{courseId, jdbcType=INTEGER}


        AND section_id = #{sectionId, jdbcType=INTEGER}


ORDER BY id

SELECT 

FROM t_user_course_section 


        AND user_id = #{userId, jdbcType=INTEGER}


        AND course_id = #{courseId, jdbcType=INTEGER}


        AND section_id = #{sectionId, jdbcType=INTEGER}


ORDER BY update_time DESC
LIMIT 0, 1

SELECT COUNT(*) 
FROM t_user_course_section
WHERE user_id = #{userId}

